mapper.select:
SELECT guid, service as service, version as version, charset as charset, sign_type as signType, mch_id as mchId, is_raw as isRaw, is_minipg as isMinipg, out_trade_no as outTradeNo, device_info as deviceInfo, op_user_id as opUserId, op_shop_id as opShopId, body as body, sub_openid as subOpenid, sub_appid as subAppid, attach as attach, total_fee as totalFee, need_receipt as needReceipt, mch_create_ip as mchCreateIp, notify_url as notifyUrl, time_start as timeStart, time_expire as timeExpire, goods_tag as goodsTag, nonce_str as nonceStr, limit_credit_pay as limitCreditPay, sign as sign, status as status, add_user_id as addUserId, add_user as addUser, add_time as addTime FROM driver_school_order where delete_flag=FALSE
AND status=#{dsOrder.status}
AND mch_id=#{dsOrder.mchId}
AND op_shop_id=#{dsOrder.opShopId}

mapper.update:
UPDATE driver_school_order set status = #{status}, mod_user = #{modUser}, mod_user_id = #{modUserId}, mod_time = now() where guid in
#{item}
and delete_flag = false
