mapper.select:
SELECT user_name AS userName, mobile_phone AS mobilePhone, id_card AS idCard, status AS status, add_user_id AS addUserId, add_user AS addUser, add_time AS addTime, mod_user_id AS modUserId, mod_user AS modUser, mod_time AS modTime, delete_flag AS deleteFlag FROM driver_school_order where delete_flag=FALSE
AND id_card=#{dsStudents.idCard}
and guid=#{dsStudents.guid}
AND user_name=#{dsStudents.userName}
