mapper.update:
UPDATE driver_school_stages set status = 1, mod_user = #{modUser}, mod_user_id = #{modUserId}, mod_time = now() where delete_flag = false and guid in
#{item}
and status = 0

UPDATE driver_school_stages set status = 0, mod_user = #{modUser}, mod_user_id = #{modUserId}, mod_time = now() where guid in
#{item}
and status = 1 and delete_flag = false

mapper.select:
SELECT guid as guid, name as name, issues as issues, status AS status, add_user_id AS addUserId, add_user AS addUser, add_time AS addTime, mod_user_id AS modUserId, mod_user AS modUser, mod_time AS modTime FROM driver_school_stages where delete_flag = false
AND name like '%#{dSstages.name}%'
and guid = #{dSstages.guid}
and add_time = #{dSstages.addTime}

SELECT guid as guid, name as name, issues as issues, status AS status, FROM driver_school_stages where delete_flag=FALSE
and status = #{dSstages.status}
and cost_id = #{dSstages.costId}

SELECT guid FROM driver_school_stages WHERE cost_id in
#{item}
AND delete_flag = FALSE
