mapper.select:
SELECT guid AS guid, user_name AS userName, mobile_phone AS mobilePhone, id_card AS idCard, status AS status, role AS role, add_user_id AS addUserId, add_user AS addUser, add_time AS addTime, mod_user_id AS modUserId, mod_user AS modUser, mod_time AS modTime, delete_flag AS deleteFlag FROM driver_school_user where role=2
AND user_name=#{dsUser.userName}
AND mobile_phone=#{dsUser.mobilePhone}
AND id_card=#{dsUser.idCard}
AND status=#{dsUser.status}
AND delete_flag=FALSE

